mapper.insert:
INSERT INTO cart (CART_ID, USER_ID, FOOD_CODE
,QUANTITY
)
VALUES (seq_cart.nextval, #{userId}, #{foodCode}
, #{quantity}
)

mapper.select:
select quantity
from cart
where user_id = #{userId} and food_code = #{foodCode}

mapper.update:
update cart
set quantity = #{quantity}
where user_id = #{userId} and food_code = #{foodCode}

update cart
set quantity = #{quantity}
where cart_id = #{cartId}
